import React, { useState } from 'react';

const CreateNote = ({ handleAddNote }) => {
    const [noteText, setNoteText] = useState('');

    const handleChange = (event) => {
        setNoteText(event.target.value);
        console.log(event.target.value);
    };

    const handleSaveClick = async () => {
        if (noteText.trim().length > 0) {
            try {
                const response = await fetch('http://127.0.0.1:5000/Create_Notes', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ text: noteText }),
                });

                if (response.ok) {
                    const newNote = await response.json();
                    handleAddNote(newNote); // Update your state or UI here
                    setNoteText('');
                } else {
                    console.error('Failed to create note');
                }
            } catch (error) {
                console.error('Error creating note:', error);
            }
        }
    };

    return (
        <div>
            <textarea value={noteText} onChange={handleChange} />
            <button onClick={handleSaveClick}>Save Note</button>
        </div>
    );
};

export default CreateNote;
