import { useRef, useState, useEffect } from 'react';


const GetNotes = (props) => {
       const handleChange = (event) => {
        setNoteText(event.target.value);
        console.log(event.target.value);
    };

      const [Notes, setNotes] = useState([]);
      useEffect(()=>{
        fetch('http://localhost:5000/get_Notes',{
          'methods':'GET',
          headers : {
            'Content-Type':'application/json'
          }
        })
        .then(response => response.json())
        .then(response => setNotes(response))
        .catch(error => console.log(error))

      },[])


    return (
        <div className="mt-2">
        {/* Display the article details if article is not None */}
        {props.articles && props.articles.map(article =>{
            return (

              <div key= {Notes.id}>
                <h2 className="text-primary"> { Notes.title} </h2>
                <p> { Notes.body } </p>
                <p> { Notes.date } </p>
                <hr/>
              </div>
            )

            })}

                 <div className='note new'>
                            <textarea
                            placeholder=' type to add a note...'
                            onChange={handleChange}
                            value={Notes}>
                            </textarea>
                            <div className='notefooter'>
                                <h6>200 remaining </h6>
                                <button className="savebtn" onClick={handleSaveClick}>Save</button>
                            </div>

                        </div>
         </div>

        )
}

export default GetNotes;
